pragma solidity ^0.5.0;

contract ReadingList {

    struct Slot {
        uint id;
        string linkId;
    }

    mapping(uint => Slot) public slots;
    uint public slotsCount;

    constructor() public {
        addSlot();
        addSlot();
        addSlot();
        addSlot();
        addSlot();
    }

    event LinkAdded(address owner);

    function addLink(
        string memory _linkId,
        uint _slot
    )
    public
    payable
    {
        require(_slot > 0 && _slot <= slotsCount);
        slots[_slot].linkId = _linkId;
        emit LinkAdded(msg.sender);

    }

    function addSlot() private {
        slotsCount ++;
        slots[slotsCount] = Slot(slotsCount,'0');
    }


}
